    window.replaceLanRes = function (res) {
        if (!res || !EChatQuery || typeof res != 'object') {//ws返回资源和console的调用资源的时候 页面都应该加载好了
            return;
        }
        replaceHtmlLan(res);
        lanRes = EChatQuery.extend(lanRes, res);
    }
